mapper.select:
SELECT A.MENU_ID AS MENU_ID , A.MENU_PID AS MENU_PID , A.QUICK_ID AS QUICK_ID , A.MENU_NM AS MENU_NM , A.LVL_NO AS LVL_NO , A.SRT_NO AS SRT_NO , A.CRE_USR_ID AS CRE_USR_ID , DATE_FORMAT(A.CRE_DTTM,'%Y%m%d%H%i%s') AS CRE_DTTM , A.MOD_USR_ID AS MOD_USR_ID , DATE_FORMAT(A.MOD_DTTM,'%Y%m%d%H%i%s') AS MOD_DTTM FROM TB_COMM_MENU A WHERE 1=1
AND A.MENU_ID = #{menuId}
AND A.QUICK_ID = #{quickId}
AND A.MENU_NM LIKE CONCAT('%',#{menuNm},'%')
ORDER BY SRT_NO ASC
